import React, { Component, useState } from 'react';
import './Calculator.css';

const Numberinput = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, '00', '000'];
const Operator = ['+', '-', '*', '/', '%', '.', '<', '='];
let Result;
class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Firstnum: 0,
      Secondnum: 0,
      isNumber: true,
      Result: 0,
    };

    this.InputNum = (x) => {
      if (this.state.isNumber) {
        this.setState({ Firstnum: x.target.innerText, isNumber: false });
      } else {
        this.setState({ Firstnum: this.state.Firstnum + x.target.innerText });
      }
      this.setState({ Result: this.state.Firstnum });
    };

    this.InputOperator = (x) => {
      if (x.target.innerText == '<') {
        this.setState({ Firstnum: 0, isNumber: true });
      }
    };
  }
  render() {
    return (
      <div className="container">
        <div className="calculator">
          <input
            type="number"
            value={this.state.Result}
            onChange={this.InputNum}
          />
          <div className="btn">
            <div className="input_btn">
              {Numberinput.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={this.InputNum}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
            <div className="operate_btn">
              {Operator.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={this.InputOperator}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Calculator;
