import React, { Component, useState } from 'react';
import './Calculator.css';

const Number = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0];
const Operator = ['+', '-', '*', '/', '%', '.', '<', '='];

class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = { Num: 0 };

    this.Compute = (x) => {
      if (typeof x != String) {
        this.setState({ Num: x.target.innerText });
      } else if (typeofx == Number) {
        console.log({ Num: x.target.innerText });
      }
    };
  }
  render() {
    return (
      <div className="container">
        <div className="calculator">
          <input type="number" value={this.state.Num} onChange={this.Compute} />
          <div className="btn">
            <div className="input_btn">
              {Number.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={this.Compute}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
            <div className="operate_btn">
              {Operator.map((x) => {
                return (
                  <button id={`input${x}`} key={`input${x}`}>
                    {x}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Calculator;
