import React, { Component, useState } from 'react';
import './Calculator.css';

const Numberinput = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0];
const Operator = ['+', '-', '*', '/', '%', '.', '<', '='];

class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = { Num: 0, Firstnumber: Num, Secondnumber: Firstnumber + Num };

    this.Compute = (x) => {
      this.setState({ Num: x.target.innerText });
    };
  }
  render() {
    return (
      <div className="container">
        <div className="calculator">
          <input value={this.state.Num} onChange={this.Compute} />
          <div className="btn">
            <div className="input_btn">
              {Numberinput.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={this.Compute}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
            <div className="operate_btn">
              {Operator.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={this.Compute}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Calculator;
