import React, { Component, useState } from 'react';
import './Calculator.css';

const Numberinput = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, '00', '000'];
const Operator = ['+', '-', '*', '/', '%', '.', '<', '='];
let Result;
class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstNum: 0,
      // isFirstNumber: true,
      secondNum: 0,
      // isSecondNumber: true,
      // Result: 0,
      // isResult: true,
      result: 0,
      operationString: 0,
      operand: '',
    };
  }

  InputOperator = (x) => {
    if (x.target.innerText == '<') {
      this.setState({ Firstnum: 0, isFirstNumber: true });
    }
  };

  inputNum = (x) => {
    const { operationString, operand, firstNum, secondNum } = this.state;
    let resultToShow;
    let operation = operationString === 0 ? x : operationString + '' + x;

    if (Numberinput.indexOf(x) > -1) {
      this.setState({ operationString: operation });
    } else if (Operator.indexOf(x) > -1) {
      if (x === '=') {
        let result;
        if (operand === '+') result = firstNum + secondNum;
        if (operand === '-') result = firstNum - secondNum;
        if (operand === '/') result = firstNum / secondNum;
        this.setState({ operationString: result });
      } else this.setState({ operand: x, operationString: operation });
    } else if (Numberinput.indexOf(x) > -1) {
      if (!operand) this.setState({ firstNum: x, operationString: operation });
      if (operand) this.setState({ secondNum: x, operationString: operation });
    }

    // if (this.state.isFirstNumber) {
    //   this.setState({
    //     Firstnum: x.target.innerText,
    //     isFirstNumber: false,
    //   });
    // } else {
    //   this.setState({
    //     Firstnum: this.state.Firstnum + x.target.innerText,
    //   });
    // }
  };
  render() {
    return (
      <div className="container">
        <div className="calculator">
          <input
            type="text"
            value={this.state.operationString}
            onChange={(event) => this.inputNum(event.target.value)}
          />
          <div className="btn">
            <div className="input_btn">
              {Numberinput.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={() => this.inputNum(x)}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
            <div className="operate_btn">
              {Operator.map((x) => {
                return (
                  <button
                    id={`input${x}`}
                    key={`input${x}`}
                    onClick={() => {
                      this.inputNum(x);
                    }}
                  >
                    {x}
                  </button>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Calculator;
